// Default config for unknown tools
const DEFAULT_CONFIG: ToolConfigEntry = {
  name: 'Unknown',
  icon: DefaultToolIcon,
  color: '#6B7280', // Gray
  bgColor: 'rgba(107, 114, 128, 0.15)',
};

interface ToolConfigResult {
  name: string;
  icon: IconComponent;
  color: string;
  bgColor: string;
}

/**
 * Get tool configuration by source identifier
 */
export function getToolConfig(source: string): ToolConfigResult {
  const normalizedSource = source.toLowerCase().replace(/\s+/g, '-');
  return TOOL_CONFIG[normalizedSource] || { ...DEFAULT_CONFIG, name: formatToolName(source) };
}

/**
 * Format tool name for display
 */
export function formatToolName(source: string): string {
  const config = TOOL_CONFIG[source.toLowerCase().replace(/\s+/g, '-')];
  if (config) return config.name;

  // Fallback: capitalize and handle hyphens
  return source
    .split('-')
    .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
    .join(' ');
}

/**
 * Get tool color by source
 */
export function getToolColor(source: string): string {
  return getToolConfig(source).color;
}

interface ToolIconProps {
  source: string;
  size?: 'sm' | 'md' | 'lg';
  showLabel?: boolean;
  className?: string;
}

/**
 * Tool Icon Component
 *
 * Renders an official brand icon for the specified AI tool/source.
 */
export function ToolIcon({
  source,
  size = 'md',
  showLabel = false,
  className = '',
}: ToolIconProps) {
  const config = getToolConfig(source);
  const Icon = config.icon;

  const sizeClasses = {
    sm: 'w-4 h-4',
    md: 'w-5 h-5',
    lg: 'w-6 h-6',
  };

  const containerSizes = {
    sm: 'w-6 h-6',
    md: 'w-8 h-8',
    lg: 'w-10 h-10',
  };

  return (
    <div className={`flex items-center gap-2 ${className}`}>
      <div
        className={`${containerSizes[size]} rounded-lg flex items-center justify-center flex-shrink-0`}
        style={{ backgroundColor: config.bgColor }}
      >
        <Icon className={sizeClasses[size]} style={{ color: config.color }} />
      </div>
      {showLabel && <span className="text-text-primary font-medium">{config.name}</span>}
    </div>
  );
}

/**
 * Tool Badge Component
 *
 * Compact badge showing tool icon with colored indicator dot.
 */
interface ToolBadgeProps {
  source: string;
  className?: string;
}

export function ToolBadge({ source, className = '' }: ToolBadgeProps) {
  const config = getToolConfig(source);
  const Icon = config.icon;

  return (
    <div
      className={`inline-flex items-center gap-1.5 px-2 py-1 rounded-md text-xs font-medium ${className}`}
      style={{ backgroundColor: config.bgColor, color: config.color }}
    >
      <Icon className="w-3.5 h-3.5" />
      <span>{config.name}</span>
    </div>
  );
}
